mapper.insert:
INSERT INTO INQUIRY(INQ_NO
				, SELLER_NO
				, MEMBER_NO
                , INQ_TITLE
                , INQ_CONTENT)
           VALUES(SEQ_INO.NEXTVAL
                , #{sellerNo}
                , #{memberNo}
                , #{inqTitle}
                , #{inqContent})

mapper.update:
UPDATE INQUIRY
   SET COUNT = COUNT + 1
 WHERE INQ_NO = #{inqNo}
   AND SELLER_NO = #{sellerNo}
   AND DELETE_DATE IS NULL

UPDATE INQUIRY_FILE
   SET DELETE_DATE = SYSDATE
 WHERE INQ_NO = #{inqNo}

UPDATE INQUIRY
   SET INQ_TITLE = #{inqTitle}
     , INQ_CONTENT = #{inqContent}
 WHERE INQ_NO = #{inqNo}
   AND DELETE_DATE IS NULL

UPDATE INQUIRY
   SET DELETE_DATE = SYSDATE
 WHERE INQ_NO = #{inquiryNo}

UPDATE INQUIRY
   SET RESPONSE_CONTENT = DEFAULT, RESPONSE_DATE = DEFAULT
 WHERE INQ_NO = #{inqNo}

UPDATE INQUIRY
   SET RESPONSE_CONTENT = #{responseContent}, RESPONSE_DATE = SYSDATE
 WHERE INQ_NO = #{inqNo}

mapper.insert:
INSERT INTO INQUIRY_REPLY(REPLY_NO
                        , INQ_NO
                        , MEMBER_NO
                        , REPLY_CONTENT
     					, TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS "CREATE_DATE")
                   VALUES(SEQ_IRNO.NEXTVAL
                        , #{inqNo}
                        , #{memberNo}
                        , #{replyContent}
                        , SYSDATE)

mapper.update:
UPDATE INQUIRY_REPLY
   SET REPLY_CONTENT = #{replyContent}, CREATE_DATE = SYSDATE
 WHERE REPLY_NO = #{replyNo}
   AND INQ_NO = #{inqNo}
