mapper.insert:
INSERT INTO MEMBER(MEMBER_NO
				 , MEMBER_ID
				 , MEMBER_PWD
				 , MEMBER_NAME
				 , EMAIL
				 , PHONE
				 , ADDRESS
				 , GENDER
				 , ZIP_CODE
				 , DETAIL_ADDRESS
				 )
		    VALUES(SEQ_MNO.NEXTVAL
		    	, #{memberId}
		    	, #{memberPwd}
		    	, #{memberName}
		    	, #{email}
		    	, #{phone}
		    	, #{address}
		    	, #{gender}
		    	, #{zipCode}
		    	, #{detailAddress})

mapper.select:

SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_ID = #{checkId}

mapper.update:
UPDATE MEMBER
   SET MEMBER_NAME = #{memberName}
   	 , EMAIL = #{email}
   	 , GENDER = #{gender}
   	 , PHONE = #{phone}
   	 , ADDRESS = #{address}
   	 , ZIP_CODE = #{zipCode}
   	 , DETAIL_ADDRESS = #{detailAddress}
WHERE MEMBER_ID = #{memberId}
  AND STATUS IS NULL

UPDATE MEMBER
   SET STATUS = SYSDATE
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER SET
MEMBER_PWD = #{memberPwd}
WHERE MEMBER_NO = #{memberNo}
