mapper.update:
UPDATE NOTICE
   SET DELETE_DATE = SYSDATE
 WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE_FILE
   SET DELETE_DATE = SYSDATE
 WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE
   SET NOTICE_TITLE = #{noticeTitle}
     , NOTICE_CONTENT = #{noticeContent}
 WHERE NOTICE_NO = #{noticeNo}
   AND DELETE_DATE IS NULL
