mapper.update:
UPDATE CART
   SET STATUS = 'Y'
 WHERE MEMBER_NO = #{memberNo}
   AND GOOD_NO = #{goodNo}

mapper.delete:
DELETE
  FROM CART
 WHERE MEMBER_NO = #{memberNo}
   AND GOOD_NO = #{goodNo}
   AND STATUS = 'N'

mapper.select:
SELECT ORDER_NO, PRICE, PAY_DATE, REQUEST
  FROM "ORDER"
 WHERE MEMBER_NO = #{memberNo}
