mapper.insert:
INSERT INTO REVIEW_REPLY(REPLY_NO
					  , REV_NO
					  , MEMBER_NO
					  , REPLY_CONTENT
					  , CREATE_DATE)
			 	 VALUES(SEQ_RRNO.NEXTVAL
			 	 	  , #{revNo}
			 	 	  , #{memberNo}
			 	 	  , #{replyContent}
			 	 	  , SYSDATE)

mapper.update:
	UPDATE REVIEW_REPLY
	   SET REPLY_CONTENT = #{replyContent}
	   	  ,CREATE_DATE = SYSDATE
 	 WHERE REV_NO = #{revNo}
	   AND REPLY_NO = #{replyNo}
	   AND MEMBER_NO = #{memberNo}

UPDATE REVIEW_REPLY
   SET DELETE_DATE = SYSDATE
 WHERE REV_NO = #{revNo}
   AND REPLY_NO = #{replyNo}
   AND MEMBER_NO = #{memberNo}

UPDATE REVIEW
   SET DELETE_DATE = SYSDATE
 WHERE REV_NO = #{rno}

UPDATE REVIEW_FILE
   SET DELETE_DATE = SYSDATE
 WHERE REV_NO = #{rno}

